<div id="nbaph_poll" class="nbaph_article_standard">   <div class="nbaph_article_title">      POLL   </div>   <div class="nbaph_article_content"><?php$results = $connect->query("select * from polls order by DatePosted desc limit 0, 1");$row = $results->fetch_array();?>      <img src="<?php echo $row['Image']; ?>" style="width: 100%" />      <div id="nbaph_poll_question">         <?php echo $row['Question']; ?>      </div>      <div id="nbaph_poll_answers">         <form id="nbaph_poll_form">            <input type="hidden" name="id" value="<?php echo $row['PollID']; ?>"><?php$results = $connect->query("select * from pollquestions where PollID = '" . $row['PollID'] . "'");$count = 1;while($row = $results->fetch_array()) {?>            <div>               <input type="radio" id="poll<?php echo $count; ?>" class="poll" name="poll" value="<?php echo $count; ?>">               <label for="poll<?php echo $count?>"><?php echo stripslashes($row['Choice']); ?></label>            </div><?php   $count += 1;}?>         </form>      </div>      <div id="nbaph_poll_submit"></div>   </div>   <div class="nbaph_article_more_link"></div></div>